sys_script_include.script:
var SelfReport = Class.create();
SelfReport.prototype = {
    initialize: function() {},
    isOpen: function(current) {
        if (current.state != "Requested") {
            return false;
        }
		var selfGR = new GlideRecord("x_879347_emergen_0_status_report");
        selfGR.addQuery("sys_id", current.self_report);
        selfGR.addQuery("status", "IN", "submitted, at_risk, status_requested");
        selfGR.query();
        if (selfGR.next()) {
            return true;
        } else {
            return false;
        }
    },
    isOpenForStatusRequest: function(current) {
        var selfGR = new GlideRecord("x_879347_emergen_0_status_report");
        selfGR.addQuery("sys_id", current.self_report);
        selfGR.addQuery("status", "IN", "submitted, at_risk");
        selfGR.query();
        if (selfGR.next()) {
            return true;
        } else {
            return false;
        }
    },
    type: 'SelfReport'
};
