sys_script_include.script:
var WorkStatusRequestTask = Class.create();
WorkStatusRequestTask.prototype = {
    initialize: function() {},
    isRequested: function(current) {
        if (current.status == "closed") {
            return false;
        }
        var statusGR = new GlideRecord("x_879347_emergen_0_work_status_request_task");
        statusGR.addQuery("self_report", current.sys_id);
        statusGR.addQuery("state", "Requested");
        statusGR.query();
        if (statusGR.next()) {
            return true;
        } else {
            return false;
        }
    },
    type: 'WorkStatusRequestTask'
};
