sys_ux_data_broker_transform.script:
function transform() {

    var data = [];
    var icon = {
        "emergency_information": "circle-exclamation-outline",
        "community_update": "megaphone-outline"
    };
	var month = {
		"1":"January",
		"2":"February",
		"3":"March",
		"4":"April",
		"5":"May",
		"6":"June",
		"7":"July",
		"8":"August",
		"9":"September",
		"10":"October",
		"11":"November",
		"12":"December"
	};
    var communityAlert = new GlideRecord('x_879347_emergen_0_emergency_alert');
    communityAlert.addActiveQuery();
    communityAlert.query();
    while (communityAlert.next()) {

        var date = new GlideDateTime(communityAlert.sys_created_on).getDisplayValue();
        var alert = {};
        var title = {};
        var fields = [];
        title.label = gs.getMessage("{0}",communityAlert['emergency_alert_title'].toString());
        title.opensWindow = 'false';
		title.href=[gs.getProperty('glide.servlet.uri'),'x/879347/x_879347_emergen_0/portal/emergency-information/x_879347_emergen_0_emergency_alert/',communityAlert.sys_id,'/',communityAlert.kb_article].join('');
        fields.push({
            "text": gs.getMessage("{0}",communityAlert.getDisplayValue('type')),
            "type": "string"
        });
        fields.push({
            "text": date.split(' ')[0].toString(),
            "type": "string"
        });
        alert.icon = icon[communityAlert['type'].toString()];
        alert.title = title;
        alert.fields = fields;
        data.push(alert);
    }

    return data;
}
